mapper.select:
SELECT R_NO, R_SEND, R_SENDNAME, R_RECEIVE, R_RECEIVENAME, R_TITLE, R_CONTENT, R_REGDATE, R_AGREE 	
FROM GALLERY.REQUEST 
WHERE R_NO=#{r_no}


SELECT R_NO, R_SEND, R_SENDNAME, R_RECEIVE, R_RECEIVENAME, R_TITLE, R_CONTENT, R_REGDATE, R_AGREE 
FROM (SELECT ROWNUM AS RN,R_NO, R_SEND, R_SENDNAME, R_RECEIVE, 
R_RECEIVENAME, R_TITLE, R_CONTENT, R_REGDATE, R_AGREE FROM GALLERY.REQUEST WHERE R_RECEIVE=#{r_receive} ORDER BY R_REGDATE DESC) WHERE CEIL(RN/7) =#{r_pcount}




		SELECT R_NO, R_SEND, R_SENDNAME, R_RECEIVE, R_RECEIVENAME, R_TITLE, R_CONTENT, R_REGDATE, R_AGREE 
FROM (SELECT ROWNUM AS RN,R_NO, R_SEND, R_SENDNAME, R_RECEIVE, 
R_RECEIVENAME, R_TITLE, R_CONTENT, R_REGDATE, R_AGREE FROM GALLERY.REQUEST WHERE R_SEND=#{r_send} ORDER BY R_REGDATE DESC) WHERE CEIL(RN/7) =#{r_pcount}

mapper.update:
UPDATE GALLERY.REQUEST 
SET R_AGREE=#{r_agree} 
WHERE R_NO=#{r_no}
